img = 0;
tot_img = NaN;
pullright = () => {
    if (img == (tot_img-1)) {

    } else {
        sel = "[jslog='index: " + (img) + "']"
        $(sel).css({
            display: "none",
        });
        img += 1;
        sel = "[jslog='index: " + (img) + "']"
        $(sel).css({
            display: "block",
        });
        btnstate()
    }
}

pullleft = () => {
    if (img == 0) {

    } else {
        sel = "[jslog='index: " + (img) + "']"
        $(sel).css({
            display: "none",
        });
        img -= 1;
        sel = "[jslog='index: " + (img) + "']"
        $(sel).css({
            display: "block",
        });
        btnstate()
    }
}

setTot = (num) => {
    tot_img = num
    console.log(tot_img+' image(s) has/have been loaded.')
}

btnstate = () => {
    if (img != tot_img && img != 0) {
        $('[data-button*="pull"]').removeClass('disabled')
    }
    if (img == 0 && img == (tot_img - 1)) {
        $('[data-button*="pull"]').addClass('disabled')
    }
    if (img == (tot_img - 1)) {
        $('[data-button="pull-right"]').addClass('disabled')
    } if (img == 0) {
        $('[data-button="pull-left"]').addClass('disabled')
    }
}